-- South Region Activities
INSERT INTO activities (name, address, region, short_description, thumbnail_url, latitude, longitude) VALUES
('Beach House Restaurant', '5022 Lawai Rd, Koloa, HI 96756', 'South', 'Upscale oceanfront restaurant featuring fresh Hawaiian seafood and spectacular sunset views. Perfect for a romantic dinner or special celebration.', 'https://images.unsplash.com/photo-1559339352-11d035aa65de?w=400', 21.8766, -159.4968),
('Lawai Beach', 'Lawai Road, Koloa, Kauai, HI 96756', 'South', 'Small secluded beach perfect for snorkeling and spotting sea turtles. Crystal clear water and excellent marine life viewing.', 'https://images.unsplash.com/photo-1559827260-dc66d52bef19?w=400', 21.8825, -159.4976),
('Poipu Beach Park', '2827 Poipu Rd, Koloa, HI 96756', 'South', 'Family-friendly beach with lifeguards, great for swimming and spotting Hawaiian monk seals. Consistently rated one of Americas best beaches.', 'https://images.unsplash.com/photo-1505142468610-359e7d316be0?w=400', 21.8768, -159.4696),
('Keoki''s Paradise', '2360 Kiahuna Plantation Dr, Koloa, HI 96756', 'South', 'Tropical garden setting with island-inspired cuisine. Famous for prime rib and fresh fish, with a lively bar scene.', 'https://images.unsplash.com/photo-1517248135467-4c7edcad34c4?w=400', 21.8825, -159.4663),
('Spouting Horn', 'Lawai Rd, Koloa, HI 96756', 'South', 'Natural blowhole that shoots water high into the air through lava rock formations. Beautiful spot for photos and watching ocean power.', 'https://images.unsplash.com/photo-1582967788606-a171c1080cb0?w=400', 21.8784, -159.5082),
('Puka Dog', '2100 Hoone Rd, Koloa, Kauai, HI 96756', 'South', 'Unique Hawaiian-style hot dogs with signature relishes and tropical fruit sauces. Quick, casual dining perfect for beach day fuel at Poipu Shopping Village.', 'https://images.squarespace-cdn.com/content/v1/5700550db09f95bfee64ca31/1603672501583-NP1L084O9J28D3TX38TI/IMG_8152.JPG?format=1500w', 21.8825, -159.4663),
('Porky''s Kauai', '5460 Koloa Rd, Koloa, Kauai, HI 96756', 'South', 'Award-winning BBQ and smoked meats in the heart of old Koloa town. Texas-style barbecue with island flair, casual outdoor seating.', 'https://images.unsplash.com/photo-1529193591184-b1d58069ecdd?w=400', 21.9066, -159.4694),
('La Spezia', '5492 Koloa Road, Koloa, HI 96756', 'South', 'Authentic Italian cuisine with fresh pasta and wood-fired pizzas. Intimate atmosphere with outdoor courtyard seating in historic Koloa.', 'https://images.unsplash.com/photo-1555396273-367ea4eb4db5?w=400', 21.9066, -159.4694),
('Merriman''s Kauai', '2829 Ala Kalanikaumaka #42, Koloa, Kauai, HI 96756', 'South', 'Farm-to-table fine dining showcasing local ingredients and sustainable Hawaii Regional Cuisine. Upscale yet relaxed ambiance at Poipu Shopping Village.', 'https://images.unsplash.com/photo-1550966871-3ed3cdb5ed0c?w=400', 21.8825, -159.4663),
('The Shops at Kukui''ula', '2829 Ala Kalanikaumaka Rd, Koloa, Hawaii 96756', 'South', 'Upscale outdoor shopping center featuring boutiques, galleries, restaurants, and weekly farmers market. Beautiful open-air setting perfect for leisurely browsing and dining.', 'https://images.unsplash.com/photo-1441986300917-64674bd600d8?w=400', 21.8825, -159.4663),
('Shipwreck Beach', 'Poipu, HI 96756', 'South', 'Dramatic beach known for powerful waves and scenic coastal cliffs. Popular spot for experienced surfers and photographers seeking stunning sunsets and rugged coastline views.', 'https://images.unsplash.com/photo-1473496169904-658ba7c44d8a?w=400', 21.8850, -159.4500),
('Koloa Fish Market', '3390 Poipu Road, Koloa, HI 96756', 'South', 'Fresh local fish, poke bowls, and plate lunches. Local favorite for quality seafood and generous portions at great prices. Perfect for a quick, delicious meal.', 'https://images.unsplash.com/photo-1615141982883-c7ad0e69fd62?w=400', 21.8894, -159.4694),
('Snorkel Bob''s Koloa', '3236 Poipu Rd, Koloa, HI 96756', 'South', 'Full-service snorkel and beach gear rental shop. Quality equipment, expert advice, and convenient location for Poipu beach adventures.', 'https://images.unsplash.com/photo-1441984904996-e0b6ba687e04?w=400', 21.8850, -159.4660),

-- North Region Activities  
('Hanalei Bay', 'Weke Rd, Hanalei, HI 96714', 'North', 'Iconic crescent-shaped bay with pristine white sand and turquoise water. Perfect for swimming, surfing, and paddleboarding with mountain backdrop.', 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=400', 22.2071, -159.5006),
('Hideaways Beach', 'Pali Ke Kua St, Princeville, HI 96722', 'North', 'Secluded beach accessible via steep trail. Excellent snorkeling and romantic setting, especially at sunset.', 'https://images.unsplash.com/photo-1473116763249-2faaef81ccda?w=400', 22.2189, -159.4842),
('Bar Acuda', '5-5161 Kuhio Hwy, Hanalei, HI 96714', 'North', 'Mediterranean tapas bar with farm-to-table ingredients. Cozy atmosphere with excellent wine selection.', 'https://images.unsplash.com/photo-1414235077428-338989a2e8c0?w=400', 22.2016, -159.5007),
('Napali Coast Boat Tour', 'Kuhio Hwy, Hanalei, HI 96714', 'North', 'Spectacular boat tour along the dramatic Na Pali coastline. See sea caves, waterfalls, and possibly dolphins and whales.', 'https://images.unsplash.com/photo-1544551763-46a013bb70d5?w=400', 22.2071, -159.5006),
('Tahiti Nui', '5-5134 Kuhio Hwy, Hanalei, HI 96714', 'North', 'Legendary local bar with live music, tropical drinks, and authentic Hawaiian atmosphere. Famous for mai tais and luaus.', 'https://images.unsplash.com/photo-1470337458703-46ad1756a187?w=400', 22.2016, -159.5007),
('Queens Bath Trailhead', 'Princeville, HI 96722', 'North', 'Scenic coastal trail leading to natural tide pools carved into lava rock. Best visited during calm summer months for swimming in the pools.', 'https://images.unsplash.com/photo-1507525428034-b723cf961d3e?w=400', 22.2189, -159.4842),
('Tiki Iniki', '5-4280 Kuhio Hwy, Princeville, HI 96722', 'North', 'Casual beachfront dining with Pacific Rim cuisine and tropical cocktails. Relaxed atmosphere with oceanfront views and live music.', 'https://images.unsplash.com/photo-1514933651103-005eec06c04b?w=400', 22.2139, -159.4869),
('The Dolphin Restaurant', '5-5016 Kuhio Highway, Hanalei, HI 96714', 'North', 'Fresh fish market and sushi bar with riverside seating. Catch of the day served simply prepared, highlighting quality ingredients and local flavors.', 'https://images.unsplash.com/photo-1579584425555-c3ce17fd4351?w=400', 22.2016, -159.5007),
('Hanalei Dolphin Fish Market', '5-5016 Kuhio Highway, Hanalei, Kauai, HI 96714', 'North', 'Fresh fish market with daily catches and sushi bar. Local institution offering quality seafood to take home or enjoy at their riverside restaurant.', 'https://images.unsplash.com/photo-1599084993091-1cb5c0721cc6?w=400', 22.2016, -159.5007),
('Hanalei Pier', 'Hanalei, HI 96714', 'North', 'Historic pier stretching into Hanalei Bay, perfect for sunset watching, fishing, and photography. Iconic landmark with stunning mountain and bay views.', 'https://images.unsplash.com/photo-1566024287286-457247b70310?w=400', 22.2071, -159.5006),

-- East Region Activities
('Wailua Falls', 'Maalo Rd, Kapaa, HI 96746', 'East', 'Stunning 173-foot waterfall easily accessible from the road. Best visited in the morning for rainbow sightings in the mist.', 'https://images.unsplash.com/photo-1432405972618-c60b0225b8f9?w=400', 22.0416, -159.3764),
('Opaekaa Falls', 'Kuamoo Rd, Kapaa, HI 96746', 'East', 'Beautiful cascading waterfall viewable from a scenic lookout. Great photo opportunity with Wailua River valley views.', 'https://images.unsplash.com/photo-1547036967-23d11aacaee0?w=400', 22.0580, -159.3904),
('Sleeping Giant Trail', 'Haleilio Rd, Kapaa, HI 96746', 'East', 'Moderate 3.5-mile hike offering panoramic views of the east coast. Named for mountain ridge resembling a resting giant.', 'https://images.unsplash.com/photo-1551632811-561732d1e306?w=400', 22.0660, -159.3873),
('Hukilau Lanai', '520 Aleka Loop, Kapaa, HI 96746', 'East', 'Farm-to-table Hawaiian cuisine with oceanfront dining. Fresh fish, local produce, and live music create perfect island ambiance.', 'https://images.unsplash.com/photo-1555396273-367ea4eb4db5?w=400', 22.0946, -159.3189),
('Lydgate Beach Park', '3540 Leho Dr, Lihue, HI 96766', 'East', 'Protected swimming area perfect for families and snorkeling. Features ancient Hawaiian ruins and beautiful coastal paths.', 'https://images.unsplash.com/photo-1519046904884-53103b34b206?w=400', 22.0507, -159.3316),
('Mark''s Place', '1610 Haleukana Street, Lihue, HI 96766', 'East', 'Local favorite serving generous portions of American comfort food and Hawaiian plate lunches. Affordable prices and authentic local atmosphere beloved by residents.', 'https://images.unsplash.com/photo-1504674900247-0877df9cc836?w=400', 21.9812, -159.3720),
('Lawaia Fish Company', '3082 Peleke St, Ste 101, Lihue, HI 96766', 'East', 'Fresh fish market and deli serving poke, sushi, and seafood plates. Known for quality local catch and authentic Hawaiian-style preparations.', 'https://images.unsplash.com/photo-1565680018434-b513d5e5fd47?w=400', 21.9742, -159.3556),
('The Fish Express', '3-3343 Kuhio Highway #7, Lihue, HI', 'East', 'Popular local fish market offering fresh poke, sushi rolls, and fish plates. Great selection of daily catch with island-style preparations at affordable prices.', 'https://images.unsplash.com/photo-1534604973900-c43ab4c2e0ab?w=400', 21.9742, -159.3556),
('The Kauai Community Market', '3-1901 Kaumualii Hwy, Lihue, HI 96766', 'East', 'Vibrant Saturday farmers market featuring local produce, crafts, food vendors, and live music. Experience authentic island culture and support local farmers and artisans.', 'https://images.unsplash.com/photo-1488459716781-31db52582fe9?w=400', 21.9742, -159.3556),
('Secret Falls Kayak and Hike', '4-484 Kuhio Hwy, Kapaʻa, HI 96746', 'East', 'Adventurous kayak up the Wailua River followed by a jungle hike to a stunning hidden waterfall. Tours depart from Coconut Marketplace offering this unforgettable 4-hour experience.', 'https://images.unsplash.com/photo-1544551763-77ef2d0cfc6c?w=400', 22.0507, -159.3316),
('Pono Market', '4-1300 Kuhio Hwy, Kapaʻa, HI 96746', 'East', 'Local favorite fish market and deli offering fresh poke, plate lunches, and Hawaiian specialties. Authentic island-style preparations at reasonable prices.', 'https://images.unsplash.com/photo-1534604973900-c43ab4c2e0ab?w=400', 22.0750, -159.3400),
('Lava Lava Beach Club', 'Kapaʻa, HI 96746', 'East', 'Beachfront restaurant and bar with island cuisine and tropical cocktails. Oceanfront dining with stunning views, casual atmosphere perfect for lunch or sunset dinner.', 'https://images.unsplash.com/photo-1552566626-52f8b828add9?w=400', 22.0750, -159.3400),

-- West Region Activities
('Waimea Canyon', 'Waimea Canyon Dr, Waimea, HI 96796', 'West', 'The "Grand Canyon of the Pacific" - stunning 3,000-foot deep canyon with red and green cliffs. Multiple lookout points along scenic drive.', 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=400', 22.0818, -159.6579),
('Polihale Beach', 'End of Kaumualii Hwy, Waimea, HI 96796', 'West', 'Remote 17-mile white sand beach at the end of the road. Spectacular sunsets and views of Ni''ihau island. Four-wheel drive recommended.', 'https://images.unsplash.com/photo-1475924156734-496f6cac6ec1?w=400', 22.0820, -159.7637),
('Salt Pond Beach Park', '4310 Lele Rd, Hanapepe, HI 96716', 'West', 'Protected swimming beach with ancient Hawaiian salt ponds still in use. Great for families, swimming, and cultural history.', 'https://images.unsplash.com/photo-1471922694854-ff1b63b20054?w=400', 21.9193, -159.5958),
('Waimea Brewing Company', '9400 Kaumualii Hwy, Waimea, HI 96796', 'West', 'Craft brewery and restaurant with locally-inspired dishes. Great spot to refuel after canyon exploration.', 'https://images.unsplash.com/photo-1436076863939-06870fe779c2?w=400', 21.9643, -159.6673),
('Hanapepe Town Friday Night Art Walk', 'Hanapepe Rd, Hanapepe, HI 96716', 'West', 'Weekly art walk featuring local galleries, live music, food trucks, and authentic small-town Hawaii charm every Friday evening.', 'https://images.unsplash.com/photo-1460661419201-fd4cecdf8a8b?w=400', 21.9088, -159.5917),
('Japanese Grandma''s Cafe', '3871 Hanapepe Rd, Hanapepe, HI 96716', 'West', 'Charming local cafe serving authentic Japanese comfort food with island touches. Known for generous portions, friendly service, and affordable prices.', 'https://images.unsplash.com/photo-1623428187969-5da2dcea5ebf?w=400', 21.9088, -159.5917),
('JoJo''s Shave Ice', 'Waimea, HI 96796', 'West', 'Local favorite shave ice stand serving refreshing Hawaiian-style shave ice with tropical flavors. Perfect treat after exploring Waimea Canyon or the west side beaches.', 'https://images.unsplash.com/photo-1563805042-7684c019e1cb?w=400', 21.9643, -159.6673);

